function colorScan(){const e=document.querySelector(".dropzone .dz-preview .dz-image img"),o=e;o.onload=(()=>{var e=new Vibrant(o).swatches();for(var n in e)if(e.hasOwnProperty(n)&&e[n]){const o=document.getElementById(n);document.getElementById(n+"-text").value=e[n].getHex(),o.style.backgroundColor=e[n].getHex(),o.innerHTML="<p onclick='copyToClipboard(\""+n+"-text\",this)'>"+e[n].getHex()+"</p>"}}),o.src=e}Dropzone.autoDiscover=!1;var myDropzone=new Dropzone("#dropzone",{acceptedFiles:"image/*",maxFiles:1,thumbnailWidth:350,thumbnailHeight:350,dictDefaultMessage:"Drop .png or .jpg here"});function copyToClipboard(e,o){const n=document.getElementById(e),t=n.value,c=o;document.querySelector(".notification");n.select(),document.execCommand("copy"),c.innerHTML="&#10003;";const r=setTimeout(function(){c.innerHTML=t,clearTimeout(r)},1500)}myDropzone.on("success",function(e){const o=document.querySelector("#previewElement"),n=new Image;n.src=e.dataURL,o.innerHTML="",o.appendChild(n)}),myDropzone.on("complete",function(e){colorScan(),myDropzone.removeFile(e)});